import React from 'react';

function ShareModal({ note, closeModal }) {
  const shareLink = `https://quicknotes.com/note/${note.id}`;

  const handleCopyLink = () => {
    navigator.clipboard.writeText(shareLink);
    alert('Link copied to clipboard!');
  };

  return (
    <div className="modal" style={{ display: 'block' }}>
      <div className="modal-content">
        <h3>{note.title}</h3>
        <p>{note.comment}</p>
        <input type="text" value={shareLink} readOnly className="form-control" />
        <button className="btn btn-success" onClick={handleCopyLink}>Copy Link</button>
        <button className="btn btn-secondary" onClick={closeModal}>Close</button>
      </div>
    </div>
  );
}

export default ShareModal;
