import React, { useState } from 'react';
import ShareModal from './ShareModal';  // Modal for sharing the note

function NoteItem({ note }) {
  const [showModal, setShowModal] = useState(false);

  const handleShare = () => {
    setShowModal(true);  // Show the modal when the share button is clicked
  };

  return (
    <div className="card shadow-sm">
      <div className="card-body">
        <h5 className="card-title">{note.title}</h5>
        <p className="card-text">{note.comment}</p>
        <button className="btn btn-primary" onClick={handleShare}>Share</button>
        <button className="btn btn-warning ml-2">Edit</button>
        <button className="btn btn-danger ml-2">Delete</button>
      </div>

      {showModal && <ShareModal note={note} closeModal={() => setShowModal(false)} />}
    </div>
  );
}

export default NoteItem;
